<div class="px py2">
    <div class="grid md:flex gap-4">  
        <div class="md:w-3/12"></div>
        <div class="md:grow">
            <div class="text-center text-keyword text-secondary-500 mb-2">Contato</div>
            <div class="text-center title mb-2">Nos mande uma <span class="text-secondary-500">Mensagem</span></div>
            <div class="mx-auto line bg-secondary-500 mb-6 md:mb-8"></div>
        </div>
        <div class="mx-auto md:w-3/12">
            <img src="assets/img/contact-dec.png" class="max-w-[180px]">
        </div>
    </div>

    <div class="bg-white rounded-xl shadow-xl p-3 md:p-6 lg:p-10 grid lg:grid-cols-5 gap-8">
        <iframe src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d4949244.307958139!2d-7.605648707718291!3d52.720945950225634!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x47d0a98a6c1ed5df%3A0xf4e19525332d8ea8!2sInglaterra%2C%20Reino%20Unido!5e0!3m2!1spt-BR!2sbr!4v1716065570698!5m2!1spt-BR!2sbr" class="w-full md:h-full md:col-span-2" height="250" style="border:0;" allowfullscreen="" loading="lazy" referrerpolicy="no-referrer-when-downgrade"></iframe>
        
        <div class="md:col-span-3 grid gap-4">
            <div class="grid grid-cols-2 lg:grid-cols-3 gap-3">
                <!-- item -->
                <div class="card-item">
                    <div class="card-item-icon">
                        <img src="data:image/png;base64,">
                    </div>
                    <div class="card-item-text">
                        010-020-0340
                    </div>
                </div>            <!-- item -->
                <div class="card-item">
                    <div class="card-item-icon">
                        <img src="data:image/png;base64,">
                    </div>
                    <div class="card-item-text">
                        contato@dplus.com
                    </div>
                </div>            <!-- item -->
                <div class="card-item col-span-2 md:col-span-1">
                    <div class="card-item-icon">
                        <img src="data:image/png;base64,">
                    </div>
                    <div class="card-item-text">
                        @digiplus
                    </div>
                </div>            <!-- item -->
            </div>

            <form>
                <div class="grid md:grid-cols-2 gap-3 mb-3">
                    <div class="form-control">
                        <label for="name">Nome</label>
                        <input type="text" name="name" required>
                    </div>
                    <div class="form-control">
                        <label for="email">Email</label>
                        <input type="email" name="email" required>
                    </div>
                </div>
                <div class="grid md:grid-cols-2 gap-3 mb-3">
                    <div class="form-control">
                        <label for="phone">Telefone</label>
                        <input type="text" name="phone" required>
                    </div>
                    <div class="form-control">
                        <label for="subject">Assunto</label>
                        <input type="text" name="subject" required>
                    </div>
                </div>
                <div class="mb-3">
                    <label for="message">Mensagem</label>
                    <textarea class="w-full h-32"></textarea>
                </div>
                <div>
                    <button class="btn btn-outline-primary">Enviar mensagem</button>
                </div>
            </form>
        </div>
    </div>
</div>
